package singleton.logger;

/**
 * SimpleDemo 类 - 简单演示类
 * 展示Logger的基本功能并验证文件写入
 * Simple demo class to show Logger basic functionality and verify file writing
 */
public class SimpleDemo {
    public static void main(String[] args) {
        System.out.println("=== Logger System Simple Demo ===\n");

        // 获取Logger实例 / Get Logger instance
        Logger logger = Logger.getInstance();

        // 写入几条日志 / Write some logs
        logger.write("First log message");
        logger.write("Second log message");
        logger.write("Third log message");

        System.out.println("\n--- Changing log file ---\n");

        // 更改日志文件 / Change log file
        logger.setFileName("demo.log");
        logger.write("Log after file change - message 1");
        logger.write("Log after file change - message 2");

        // 关闭日志器 / Close logger
        logger.close();

        System.out.println("\n=== Demo Finished ===");
        System.out.println("\nPlease check these files in logs directory:");
        System.out.println("1. application.log (should have 3 lines)");
        System.out.println("2. demo.log (should have 2 lines)");
    }
}
